module.exports = function(selector, func) {
  'use strict';

  func = (typeof func === 'undefined') ? null : func;

  var elements = document.querySelectorAll(selector);

  if (elements.length > 0) {
    if (elements.length > 1) {
      elements.forEach(function(element) {
        func(element);
      });
    }
    func(elements[0]);
  }
};
